import React, { useState, useRef, useEffect } from 'react';

// Minimal, mobile-app inspired, step-by-step guided demo
// Only generic brands, placeholder images, locked-down interactions

const BRANDS = [
  {
    id: 1,
    name: 'Brandly',
    logo: 'https://placehold.co/60x60?text=Logo',
    offer: '20% off your first order',
    image: 'https://placehold.co/300x180?text=Offer',
    description: 'Save on your first purchase with Brandly. For students only.',
    code: 'BRANDLY20',
    expires: '31/12/2024',
    conditions: [
      'For new users only',
      'Not combinable with other offers',
      'Student verification required'
    ]
  },
  {
    id: 2,
    name: 'Shopster',
    logo: 'https://placehold.co/60x60?text=Logo',
    offer: 'Free shipping on all items',
    image: 'https://placehold.co/300x180?text=Offer',
    description: 'Enjoy free shipping on any order. Students only.',
    code: 'SHIP4FREE',
    expires: '30/09/2024',
    conditions: [
      'No minimum purchase',
      'Student verification required',
      'Valid once per user'
    ]
  },
  {
    id: 3,
    name: 'Foodio',
    logo: 'https://placehold.co/60x60?text=Logo',
    offer: 'Buy 1 Get 1 Free',
    image: 'https://placehold.co/300x180?text=Offer',
    description: 'Order any meal and get another free. For students.',
    code: 'FOODIOB1G1',
    expires: '31/12/2024',
    conditions: [
      'Valid on selected menu items',
      'Student verification required',
      'Not valid with other promos'
    ]
  }
];

const STEPS = [
  {
    title: 'Welcome to Dommio!',
    description: 'This is a guided demo. Tap "Next" to begin.',
    highlight: null
  },
  {
    title: 'Student Verification',
    description: 'Tap "Verify" to simulate student verification.',
    highlight: 'verify-btn'
  },
  {
    title: 'Browse Offers',
    description: 'Tap the highlighted offer to view details.',
    highlight: 'offer-card-0'
  },
  {
    title: 'Get Your Code',
    description: 'Tap "Copy Code" to copy your discount code.',
    highlight: 'copy-code-btn'
  },
  {
    title: "That's it!",
    description: 'You\'ve completed the demo. Tap "Restart" to try again.',
    highlight: null
  }
];

const ACCENT = '#2D72D2';
const CTA = '#FF6B6B';
const BG = '#F6F7F9';
const CARD = '#fff';
const BORDER = '#E5E7EB';
const TEXT = '#1A202C';
const SUBTLE = '#6B7280';
const RADIUS = 28;

function useLockdown(refs, currentStep) {
  useEffect(() => {
    // Lock all except the highlighted ref
    Object.entries(refs).forEach(([key, ref]) => {
      if (!ref.current) return;
      if (STEPS[currentStep].highlight === key) {
        ref.current.style.pointerEvents = 'auto';
        ref.current.style.filter = 'none';
        ref.current.style.opacity = '1';
      } else {
        ref.current.style.pointerEvents = 'none';
        ref.current.style.filter = 'blur(2px) grayscale(0.2)';
        ref.current.style.opacity = '0.5';
      }
    });
  }, [refs, currentStep]);
}

export default function DommioDemo() {
  const [step, setStep] = useState(0);
  const [verified, setVerified] = useState(false);
  const [showDetails, setShowDetails] = useState(false);
  const [copied, setCopied] = useState(false);

  // Refs for lockdown
  const verifyBtnRef = useRef();
  const offerCardRef = useRef();
  const copyCodeBtnRef = useRef();
  const refs = {
    'verify-btn': verifyBtnRef,
    'offer-card-0': offerCardRef,
    'copy-code-btn': copyCodeBtnRef
  };
  useLockdown(refs, step);

  // Reset demo
  function restart() {
    setStep(0);
    setVerified(false);
    setShowDetails(false);
    setCopied(false);
  }

  // Simulate ripple effect on cards
  function ripple(e) {
    const card = e.currentTarget;
    const circle = document.createElement('span');
    const diameter = Math.max(card.clientWidth, card.clientHeight);
    const radius = diameter / 2;
    circle.style.width = circle.style.height = `${diameter}px`;
    circle.style.left = `${e.clientX - card.getBoundingClientRect().left - radius}px`;
    circle.style.top = `${e.clientY - card.getBoundingClientRect().top - radius}px`;
    circle.style.position = 'absolute';
    circle.style.background = 'rgba(45,114,210,0.12)';
    circle.style.borderRadius = '50%';
    circle.style.transform = 'scale(0)';
    circle.style.animation = 'ripple 0.5s linear';
    circle.style.pointerEvents = 'none';
    card.appendChild(circle);
    setTimeout(() => circle.remove(), 500);
  }

  // App frame
  return (
    <div style={{
      minHeight: '100vh',
      background: BG,
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: 'Inter, system-ui, sans-serif',
      color: TEXT,
      padding: 0,
    }}>
      {/* Simulated phone frame */}
      <div style={{
        width: '100%',
        maxWidth: 420,
        minHeight: 700,
        background: CARD,
        borderRadius: RADIUS,
        boxShadow: '0 8px 40px 0 rgba(0,0,0,0.13)',
        overflow: 'hidden',
        position: 'relative',
        display: 'flex',
        flexDirection: 'column',
        border: `1.5px solid ${BORDER}`,
      }}>
        {/* Status bar */}
        <div style={{height: 28, background: BG, display: 'flex', alignItems: 'center', justifyContent: 'flex-end', padding: '0 18px', fontSize: 13, color: SUBTLE, letterSpacing: 1}}>
          <span style={{marginLeft: 'auto'}}>9:41</span>
          <span style={{marginLeft: 12}}>◉ ◉ ◉</span>
        </div>
        {/* Header bar */}
        <div style={{height: 56, background: CARD, display: 'flex', alignItems: 'center', justifyContent: 'center', boxShadow: '0 1px 0 0 #F1F1F1', fontWeight: 800, fontSize: 22, letterSpacing: -1, color: TEXT}}>
          Dommio
        </div>
        {/* Stepper */}
        <div style={{display: 'flex', justifyContent: 'center', gap: 8, margin: '18px 0 0 0'}}>
          {STEPS.map((s, i) => (
            <div key={i} style={{width: 10, height: 10, borderRadius: 5, background: i === step ? ACCENT : BORDER, opacity: i <= step ? 1 : 0.3, transition: 'background 0.2s'}} />
          ))}
        </div>
        {/* Main content */}
        <div style={{flex: 1, display: 'flex', flexDirection: 'column', justifyContent: 'flex-start', alignItems: 'stretch', padding: '0 0 80px 0'}}>
          <div style={{padding: '28px 24px 0 24px'}}>
            <div style={{fontWeight: 700, fontSize: 22, marginBottom: 6, letterSpacing: -0.5}}>{STEPS[step].title}</div>
            <div style={{color: SUBTLE, fontSize: 16, marginBottom: 18, lineHeight: 1.5}}>{STEPS[step].description}</div>

            {/* Step 1: Verification */}
            {step === 1 && (
              <button
                ref={verifyBtnRef}
                style={{
                  width: '100%',
                  padding: '18px',
                  background: ACCENT,
                  color: '#fff',
                  border: 'none',
                  borderRadius: 16,
                  fontWeight: 700,
                  fontSize: 18,
                  boxShadow: '0 2px 8px rgba(45,114,210,0.10)',
                  margin: '40px 0 0 0',
                  cursor: 'pointer',
                  transition: 'background 0.2s',
                  letterSpacing: 0.2,
                }}
                onClick={() => {
                  setVerified(true);
                  setStep(2);
                }}
              >
                Verify as Student
              </button>
            )}

            {/* Step 2: Offer List */}
            {step === 2 && (
              <div
                ref={offerCardRef}
                style={{
                  background: '#F6F7F9',
                  borderRadius: 20,
                  boxShadow: '0 2px 12px 0 rgba(45,114,210,0.04)',
                  padding: 0,
                  margin: '36px 0 0 0',
                  cursor: 'pointer',
                  border: `1.5px solid ${BORDER}`,
                  minHeight: 110,
                  position: 'relative',
                  overflow: 'hidden',
                  display: 'flex',
                  alignItems: 'center',
                  transition: 'box-shadow 0.2s',
                }}
                onClick={e => { ripple(e); setShowDetails(true); }}
              >
                <img src={BRANDS[0].logo} alt="logo" style={{width: 60, height: 60, borderRadius: 16, background: '#fff', border: `1.5px solid ${BORDER}`, margin: '18px'}} />
                <div style={{flex: 1, minWidth: 0}}>
                  <div style={{fontWeight: 700, fontSize: 18, marginBottom: 2, color: TEXT, letterSpacing: -0.5}}>{BRANDS[0].name}</div>
                  <div style={{fontSize: 16, color: TEXT, fontWeight: 500, whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis'}}>{BRANDS[0].offer}</div>
                </div>
                <span style={{fontSize: 28, color: ACCENT, fontWeight: 700, margin: '0 18px 0 0'}}>&#8594;</span>
              </div>
            )}

            {/* Step 3: Offer Details */}
            {step === 3 && showDetails && (
              <div style={{marginTop: 32, background: '#F6F7F9', borderRadius: 22, boxShadow: '0 2px 12px 0 rgba(45,114,210,0.04)', border: `1.5px solid ${BORDER}`, overflow: 'hidden'}}>
                <img src={BRANDS[0].image} alt="offer" style={{width: '100%', height: 140, objectFit: 'cover', borderTopLeftRadius: 22, borderTopRightRadius: 22}} />
                <div style={{padding: 22}}>
                  <div style={{display: 'flex', alignItems: 'center', gap: 12, marginBottom: 10}}>
                    <img src={BRANDS[0].logo} alt="logo" style={{width: 38, height: 38, borderRadius: 10, background: '#fff', border: `1.5px solid ${BORDER}`}} />
                    <div style={{fontWeight: 700, fontSize: 18}}>{BRANDS[0].name}</div>
                  </div>
                  <div style={{fontWeight: 700, fontSize: 19, marginBottom: 8}}>{BRANDS[0].offer}</div>
                  <div style={{fontSize: 16, color: SUBTLE, marginBottom: 12}}>{BRANDS[0].description}</div>
                  <div style={{fontSize: 14, color: SUBTLE, marginBottom: 10}}>Expires: {BRANDS[0].expires}</div>
                  <ul style={{fontSize: 14, color: SUBTLE, marginBottom: 16, paddingLeft: 20}}>
                    {BRANDS[0].conditions.map((c, i) => <li key={i}>{c}</li>)}
                  </ul>
                  <button
                    ref={copyCodeBtnRef}
                    style={{
                      width: '100%',
                      padding: '16px',
                      background: copied ? '#1EE0B7' : CTA,
                      color: '#fff',
                      border: 'none',
                      borderRadius: 12,
                      fontWeight: 700,
                      fontSize: 18,
                      marginTop: 8,
                      cursor: 'pointer',
                      transition: 'background 0.2s',
                    }}
                    onClick={() => {
                      navigator.clipboard.writeText(BRANDS[0].code);
                      setCopied(true);
                      setTimeout(() => setCopied(false), 1200);
                      setStep(4);
                    }}
                  >
                    {copied ? 'Copied!' : 'Copy Code'}
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
        {/* Floating Bottom Bar */}
        <div
          style={{
            position: 'absolute',
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: 100,
            display: 'flex',
            justifyContent: 'center',
            background: 'rgba(255,255,255,0.97)',
            boxShadow: '0 -2px 16px 0 rgba(0,0,0,0.04)',
            padding: '18px 0',
            borderBottomLeftRadius: RADIUS,
            borderBottomRightRadius: RADIUS,
          }}
        >
          <div style={{width: '100%', maxWidth: 420, display: 'flex', justifyContent: 'center'}}>
            {step === 0 && (
              <button
                style={{
                  width: '92%',
                  padding: '18px',
                  background: ACCENT,
                  color: '#fff',
                  border: 'none',
                  borderRadius: 16,
                  fontWeight: 700,
                  fontSize: 18,
                  boxShadow: '0 2px 8px rgba(45,114,210,0.10)',
                  cursor: 'pointer',
                  transition: 'background 0.2s',
                }}
                onClick={() => setStep(1)}
              >
                Next
              </button>
            )}
            {step === 4 && (
              <button
                style={{
                  width: '92%',
                  padding: '18px',
                  background: ACCENT,
                  color: '#fff',
                  border: 'none',
                  borderRadius: 16,
                  fontWeight: 700,
                  fontSize: 18,
                  boxShadow: '0 2px 8px rgba(45,114,210,0.10)',
                  cursor: 'pointer',
                  transition: 'background 0.2s',
                }}
                onClick={restart}
              >
                Restart Demo
              </button>
            )}
          </div>
        </div>
      </div>
      {/* Ripple effect keyframes */}
      <style>{`
        @keyframes ripple {
          to { transform: scale(2.5); opacity: 0; }
        }
      `}</style>
    </div>
  );
}
