import React, { useState, useRef, useEffect } from 'react';

// Minimal, mobile-app inspired, step-by-step guided demo
// Only generic brands, placeholder images, locked-down interactions

const BRANDS = [
  {
    id: 1,
    name: 'Brandly',
    logo: 'https://placehold.co/60x60?text=Logo',
    offer: '20% off your first order',
    image: 'https://placehold.co/300x180?text=Offer',
    description: 'Save on your first purchase with Brandly. For students only.',
    code: 'BRANDLY20',
    expires: '31/12/2024',
    conditions: [
      'For new users only',
      'Not combinable with other offers',
      'Student verification required'
    ]
  },
  {
    id: 2,
    name: 'Shopster',
    logo: 'https://placehold.co/60x60?text=Logo',
    offer: 'Free shipping on all items',
    image: 'https://placehold.co/300x180?text=Offer',
    description: 'Enjoy free shipping on any order. Students only.',
    code: 'SHIP4FREE',
    expires: '30/09/2024',
    conditions: [
      'No minimum purchase',
      'Student verification required',
      'Valid once per user'
    ]
  },
  {
    id: 3,
    name: 'Foodio',
    logo: 'https://placehold.co/60x60?text=Logo',
    offer: 'Buy 1 Get 1 Free',
    image: 'https://placehold.co/300x180?text=Offer',
    description: 'Order any meal and get another free. For students.',
    code: 'FOODIOB1G1',
    expires: '31/12/2024',
    conditions: [
      'Valid on selected menu items',
      'Student verification required',
      'Not valid with other promos'
    ]
  }
];

const STEPS = [
  {
    title: 'Welcome to Dommio!',
    description: 'This is a guided demo. Tap "Next" to begin.',
    highlight: null
  },
  {
    title: 'Student Verification',
    description: 'Tap "Verify" to simulate student verification.',
    highlight: 'verify-btn'
  },
  {
    title: 'Browse Offers',
    description: 'Tap the highlighted offer to view details.',
    highlight: 'offer-card-0'
  },
  {
    title: 'Get Your Code',
    description: 'Tap "Copy Code" to copy your discount code.',
    highlight: 'copy-code-btn'
  },
  {
    title: "That's it!",
    description: 'You\'ve completed the demo. Tap "Restart" to try again.',
    highlight: null
  }
];

const ACCENT = '#2D72D2';
const CTA = '#FF6B6B';
const BG = '#fff';
const BORDER = '#E5E7EB';
const TEXT = '#1A202C';
const SUBTLE = '#6B7280';
const RADIUS = '20px';

function useLockdown(refs, currentStep) {
  useEffect(() => {
    // Lock all except the highlighted ref
    Object.entries(refs).forEach(([key, ref]) => {
      if (!ref.current) return;
      if (STEPS[currentStep].highlight === key) {
        ref.current.style.pointerEvents = 'auto';
        ref.current.style.filter = 'none';
        ref.current.style.opacity = '1';
      } else {
        ref.current.style.pointerEvents = 'none';
        ref.current.style.filter = 'blur(2px) grayscale(0.2)';
        ref.current.style.opacity = '0.5';
      }
    });
  }, [refs, currentStep]);
}

export default function DommioDemo() {
  const [step, setStep] = useState(0);
  const [verified, setVerified] = useState(false);
  const [showDetails, setShowDetails] = useState(false);
  const [copied, setCopied] = useState(false);

  // Refs for lockdown
  const verifyBtnRef = useRef();
  const offerCardRef = useRef();
  const copyCodeBtnRef = useRef();
  const refs = {
    'verify-btn': verifyBtnRef,
    'offer-card-0': offerCardRef,
    'copy-code-btn': copyCodeBtnRef
  };
  useLockdown(refs, step);

  // Reset demo
  function restart() {
    setStep(0);
    setVerified(false);
    setShowDetails(false);
    setCopied(false);
  }

  // UI
  return (
    <div
      style={{
        minHeight: '100vh',
        background: BG,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        fontFamily: 'Inter, system-ui, sans-serif',
        color: TEXT,
        padding: '0',
      }}
    >
      <div
        style={{
          width: '100%',
          maxWidth: 420,
          margin: '32px 0 0 0',
          borderRadius: RADIUS,
          boxShadow: '0 4px 32px 0 rgba(0,0,0,0.07)',
          background: BG,
          padding: 0,
          overflow: 'hidden',
          border: `1px solid ${BORDER}`,
        }}
      >
        {/* Header */}
        <div style={{padding: '32px 24px 16px 24px', textAlign: 'center'}}>
          <div style={{fontWeight: 700, fontSize: 28, letterSpacing: -1, marginBottom: 8}}>Dommio</div>
          <div style={{fontSize: 16, color: SUBTLE, fontWeight: 500}}>Student Savings Demo</div>
        </div>
        {/* Stepper */}
        <div style={{display: 'flex', justifyContent: 'center', gap: 8, margin: '0 0 16px 0'}}>
          {STEPS.map((s, i) => (
            <div key={i} style={{width: 10, height: 10, borderRadius: 5, background: i === step ? ACCENT : BORDER, opacity: i <= step ? 1 : 0.3}} />
          ))}
        </div>
        {/* Main Card */}
        <div style={{padding: '0 24px 24px 24px'}}>
          {/* Step Title & Description */}
          <div style={{marginBottom: 18}}>
            <div style={{fontWeight: 700, fontSize: 20, marginBottom: 4}}>{STEPS[step].title}</div>
            <div style={{color: SUBTLE, fontSize: 15, lineHeight: 1.5}}>{STEPS[step].description}</div>
          </div>

          {/* Step 1: Verification */}
          {step === 1 && (
            <button
              ref={verifyBtnRef}
              style={{
                width: '100%',
                padding: '16px',
                background: ACCENT,
                color: '#fff',
                border: 'none',
                borderRadius: 12,
                fontWeight: 700,
                fontSize: 17,
                boxShadow: '0 2px 8px rgba(45,114,210,0.08)',
                margin: '32px 0 0 0',
                cursor: 'pointer',
                transition: 'background 0.2s',
              }}
              onClick={() => {
                setVerified(true);
                setStep(2);
              }}
            >
              Verify as Student
            </button>
          )}

          {/* Step 2: Offer List */}
          {step === 2 && (
            <div
              ref={offerCardRef}
              style={{
                background: '#F9FAFB',
                borderRadius: 16,
                boxShadow: '0 1px 4px 0 rgba(0,0,0,0.03)',
                padding: 18,
                display: 'flex',
                alignItems: 'center',
                gap: 16,
                margin: '32px 0 0 0',
                cursor: 'pointer',
                border: `1px solid ${BORDER}`,
                minHeight: 90,
              }}
              onClick={() => setShowDetails(true)}
            >
              <img src={BRANDS[0].logo} alt="logo" style={{width: 48, height: 48, borderRadius: 12, background: '#fff', border: `1px solid ${BORDER}`}} />
              <div style={{flex: 1}}>
                <div style={{fontWeight: 700, fontSize: 16, marginBottom: 2}}>{BRANDS[0].name}</div>
                <div style={{fontSize: 15, color: TEXT}}>{BRANDS[0].offer}</div>
              </div>
              <span style={{fontSize: 22, color: ACCENT, fontWeight: 700}}>&#8594;</span>
            </div>
          )}

          {/* Step 3: Offer Details */}
          {step === 3 && showDetails && (
            <div style={{marginTop: 24, background: '#F9FAFB', borderRadius: 16, boxShadow: '0 1px 4px 0 rgba(0,0,0,0.03)', border: `1px solid ${BORDER}`}}>
              <img src={BRANDS[0].image} alt="offer" style={{width: '100%', height: 120, objectFit: 'cover', borderTopLeftRadius: 16, borderTopRightRadius: 16}} />
              <div style={{padding: 18}}>
                <div style={{display: 'flex', alignItems: 'center', gap: 10, marginBottom: 8}}>
                  <img src={BRANDS[0].logo} alt="logo" style={{width: 32, height: 32, borderRadius: 8, background: '#fff', border: `1px solid ${BORDER}`}} />
                  <div style={{fontWeight: 700, fontSize: 16}}>{BRANDS[0].name}</div>
                </div>
                <div style={{fontWeight: 600, fontSize: 17, marginBottom: 6}}>{BRANDS[0].offer}</div>
                <div style={{fontSize: 15, color: SUBTLE, marginBottom: 10}}>{BRANDS[0].description}</div>
                <div style={{fontSize: 13, color: SUBTLE, marginBottom: 10}}>Expires: {BRANDS[0].expires}</div>
                <ul style={{fontSize: 13, color: SUBTLE, marginBottom: 14, paddingLeft: 18}}>
                  {BRANDS[0].conditions.map((c, i) => <li key={i}>{c}</li>)}
                </ul>
                <button
                  ref={copyCodeBtnRef}
                  style={{
                    width: '100%',
                    padding: '14px',
                    background: copied ? '#1EE0B7' : CTA,
                    color: '#fff',
                    border: 'none',
                    borderRadius: 10,
                    fontWeight: 700,
                    fontSize: 16,
                    marginTop: 6,
                    cursor: 'pointer',
                    transition: 'background 0.2s',
                  }}
                  onClick={() => {
                    navigator.clipboard.writeText(BRANDS[0].code);
                    setCopied(true);
                    setTimeout(() => setCopied(false), 1200);
                    setStep(4);
                  }}
                >
                  {copied ? 'Copied!' : 'Copy Code'}
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Floating Bottom Bar */}
      <div
        style={{
          position: 'fixed',
          left: 0,
          right: 0,
          bottom: 0,
          zIndex: 100,
          display: 'flex',
          justifyContent: 'center',
          background: 'rgba(255,255,255,0.95)',
          boxShadow: '0 -2px 16px 0 rgba(0,0,0,0.04)',
          padding: '16px 0',
        }}
      >
        <div style={{width: '100%', maxWidth: 420, display: 'flex', justifyContent: 'center'}}>
          {step === 0 && (
            <button
              style={{
                width: '90%',
                padding: '16px',
                background: ACCENT,
                color: '#fff',
                border: 'none',
                borderRadius: 14,
                fontWeight: 700,
                fontSize: 17,
                boxShadow: '0 2px 8px rgba(45,114,210,0.08)',
                cursor: 'pointer',
                transition: 'background 0.2s',
              }}
              onClick={() => setStep(1)}
            >
              Next
            </button>
          )}
          {step === 4 && (
            <button
              style={{
                width: '90%',
                padding: '16px',
                background: ACCENT,
                color: '#fff',
                border: 'none',
                borderRadius: 14,
                fontWeight: 700,
                fontSize: 17,
                boxShadow: '0 2px 8px rgba(45,114,210,0.08)',
                cursor: 'pointer',
                transition: 'background 0.2s',
              }}
              onClick={restart}
            >
              Restart Demo
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
