import React, { useState, useRef, useEffect } from 'react';

// SVGs for playful icons and hero illustration
const HeroSVG = () => (
  <svg width="180" height="120" viewBox="0 0 180 120" fill="none" xmlns="http://www.w3.org/2000/svg">
    <ellipse cx="90" cy="110" rx="70" ry="10" fill="#E9EAFB"/>
    <rect x="60" y="40" width="60" height="40" rx="20" fill="#B4B8F8"/>
    <circle cx="90" cy="60" r="20" fill="#F7D6E0"/>
    <ellipse cx="90" cy="60" rx="8" ry="8" fill="#fff"/>
    <rect x="80" y="80" width="20" height="8" rx="4" fill="#FFD6A5"/>
  </svg>
);
const BrandIcon = () => (
  <svg width="40" height="40" viewBox="0 0 40 40" fill="none" xmlns="http://www.w3.org/2000/svg">
    <rect width="40" height="40" rx="12" fill="#E9EAFB"/>
    <circle cx="20" cy="20" r="10" fill="#B4B8F8"/>
    <circle cx="20" cy="20" r="5" fill="#fff"/>
  </svg>
);
const GiftIcon = () => (
  <svg width="28" height="28" viewBox="0 0 28 28" fill="none" xmlns="http://www.w3.org/2000/svg">
    <rect x="4" y="12" width="20" height="10" rx="3" fill="#FFD6A5"/>
    <rect x="7" y="7" width="14" height="7" rx="3.5" fill="#B4B8F8"/>
    <rect x="13" y="7" width="2" height="15" rx="1" fill="#fff"/>
  </svg>
);
const UserIcon = () => (
  <svg width="28" height="28" viewBox="0 0 28 28" fill="none" xmlns="http://www.w3.org/2000/svg">
    <circle cx="14" cy="10" r="6" fill="#B4B8F8"/>
    <rect x="5" y="18" width="18" height="6" rx="3" fill="#E9EAFB"/>
  </svg>
);
const HomeIcon = ({active}) => (
  <svg width="28" height="28" viewBox="0 0 28 28" fill="none" xmlns="http://www.w3.org/2000/svg">
    <rect x="6" y="12" width="16" height="10" rx="3" fill={active ? '#B4B8F8' : '#E9EAFB'}/>
    <polygon points="14,6 24,14 4,14" fill={active ? '#FFD6A5' : '#F7D6E0'}/>
  </svg>
);
const StarIcon = () => (
  <svg width="28" height="28" viewBox="0 0 28 28" fill="none" xmlns="http://www.w3.org/2000/svg">
    <polygon points="14,4 17,12 26,12 18.5,17 21,25 14,20 7,25 9.5,17 2,12 11,12" fill="#FFD6A5"/>
  </svg>
);

const BRANDS = [
  {
    id: 1,
    name: 'Brandly',
    offer: '20% off your first order',
    description: 'Save on your first purchase with Brandly. For students only.',
    code: 'BRANDLY20',
    expires: '31/12/2024',
    conditions: [
      'For new users only',
      'Not combinable with other offers',
      'Student verification required'
    ]
  }
];

const STEPS = [
  {
    title: 'Welcome to Dommio!',
    description: 'Discover exclusive student offers. Tap "Next" to begin.',
    highlight: null
  },
  {
    title: 'Student Verification',
    description: 'Tap "Verify" to simulate student verification.',
    highlight: 'verify-btn'
  },
  {
    title: 'Browse Offers',
    description: 'Tap the highlighted offer to view details.',
    highlight: 'offer-card-0'
  },
  {
    title: 'Get Your Code',
    description: 'Tap "Copy Code" to copy your discount code.',
    highlight: 'copy-code-btn'
  },
  {
    title: "That's it!",
    description: 'You\'ve completed the demo. Tap "Restart" to try again.',
    highlight: null
  }
];

const ACCENT = '#7B6EF6';
const CTA = '#FFD6A5';
const BG = 'linear-gradient(180deg, #F7F8FC 0%, #F1F3FA 100%)';
const CARD = '#fff';
const BORDER = '#E5E7EB';
const TEXT = '#232946';
const SUBTLE = '#6B7280';
const RADIUS = 28;

function useLockdown(refs, currentStep) {
  useEffect(() => {
    Object.entries(refs).forEach(([key, ref]) => {
      if (!ref.current) return;
      if (STEPS[currentStep].highlight === key) {
        ref.current.style.pointerEvents = 'auto';
        ref.current.style.filter = 'none';
        ref.current.style.opacity = '1';
      } else {
        ref.current.style.pointerEvents = 'none';
        ref.current.style.filter = 'blur(2px) grayscale(0.2)';
        ref.current.style.opacity = '0.5';
      }
    });
  }, [refs, currentStep]);
}

export default function DommioDemo() {
  const [step, setStep] = useState(0);
  const [verified, setVerified] = useState(false);
  const [showDetails, setShowDetails] = useState(false);
  const [copied, setCopied] = useState(false);

  // Refs for lockdown
  const verifyBtnRef = useRef();
  const offerCardRef = useRef();
  const copyCodeBtnRef = useRef();
  const refs = {
    'verify-btn': verifyBtnRef,
    'offer-card-0': offerCardRef,
    'copy-code-btn': copyCodeBtnRef
  };
  useLockdown(refs, step);

  function restart() {
    setStep(0);
    setVerified(false);
    setShowDetails(false);
    setCopied(false);
  }

  // App frame
  return (
    <div style={{
      minHeight: '100vh',
      background: BG,
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: 'Inter, system-ui, sans-serif',
      color: TEXT,
      padding: 0,
    }}>
      {/* Simulated phone frame */}
      <div style={{
        width: '100%',
        maxWidth: 420,
        minHeight: 700,
        background: CARD,
        borderRadius: RADIUS,
        boxShadow: '0 8px 40px 0 rgba(123,110,246,0.10)',
        overflow: 'hidden',
        position: 'relative',
        display: 'flex',
        flexDirection: 'column',
        border: `1.5px solid ${BORDER}`,
      }}>
        {/* Header bar */}
        <div style={{height: 64, background: 'linear-gradient(90deg, #B4B8F8 0%, #FFD6A5 100%)', display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '0 24px', boxShadow: '0 1px 0 0 #F1F1F1'}}>
          <div style={{display: 'flex', alignItems: 'center', gap: 10}}>
            <GiftIcon />
            <span style={{fontWeight: 800, fontSize: 22, letterSpacing: -1, color: TEXT}}>Dommio</span>
          </div>
          <UserIcon />
        </div>
        {/* Hero illustration and greeting */}
        {step === 0 && (
          <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', padding: '32px 0 0 0'}}>
            <HeroSVG />
            <div style={{fontWeight: 800, fontSize: 26, margin: '24px 0 8px 0', color: TEXT}}>Welcome!</div>
            <div style={{fontSize: 17, color: SUBTLE, textAlign: 'center', maxWidth: 260, marginBottom: 8}}>Discover exclusive student offers and save on your favorite brands.</div>
          </div>
        )}
        {/* Stepper */}
        <div style={{display: 'flex', justifyContent: 'center', gap: 8, margin: '18px 0 0 0'}}>
          {STEPS.map((s, i) => (
            <div key={i} style={{width: 10, height: 10, borderRadius: 5, background: i === step ? ACCENT : BORDER, opacity: i <= step ? 1 : 0.3, transition: 'background 0.2s'}} />
          ))}
        </div>
        {/* Main content */}
        <div style={{flex: 1, display: 'flex', flexDirection: 'column', justifyContent: 'flex-start', alignItems: 'stretch', padding: '0 0 90px 0'}}>
          <div style={{padding: '28px 24px 0 24px'}}>
            <div style={{fontWeight: 700, fontSize: 22, marginBottom: 6, letterSpacing: -0.5}}>{STEPS[step].title}</div>
            <div style={{color: SUBTLE, fontSize: 16, marginBottom: 18, lineHeight: 1.5}}>{STEPS[step].description}</div>

            {/* Step 1: Verification */}
            {step === 1 && (
              <button
                ref={verifyBtnRef}
                style={{
                  width: '100%',
                  padding: '20px',
                  background: ACCENT,
                  color: '#fff',
                  border: 'none',
                  borderRadius: 18,
                  fontWeight: 700,
                  fontSize: 19,
                  boxShadow: '0 2px 8px rgba(123,110,246,0.10)',
                  margin: '40px 0 0 0',
                  cursor: 'pointer',
                  transition: 'background 0.2s',
                  letterSpacing: 0.2,
                }}
                onClick={() => {
                  setVerified(true);
                  setStep(2);
                }}
              >
                <span style={{marginRight: 10, verticalAlign: 'middle'}}><StarIcon /></span>
                Verify as Student
              </button>
            )}

            {/* Step 2: Offer List */}
            {step === 2 && (
              <div
                ref={offerCardRef}
                style={{
                  background: 'linear-gradient(90deg, #B4B8F8 0%, #FFD6A5 100%)',
                  borderRadius: 22,
                  boxShadow: '0 2px 12px 0 rgba(123,110,246,0.08)',
                  padding: 0,
                  margin: '36px 0 0 0',
                  cursor: 'pointer',
                  border: `1.5px solid ${BORDER}`,
                  minHeight: 120,
                  position: 'relative',
                  overflow: 'hidden',
                  display: 'flex',
                  alignItems: 'center',
                  transition: 'box-shadow 0.2s',
                }}
                onClick={() => setShowDetails(true)}
              >
                <div style={{margin: '18px'}}><BrandIcon /></div>
                <div style={{flex: 1, minWidth: 0}}>
                  <div style={{fontWeight: 800, fontSize: 19, marginBottom: 2, color: TEXT, letterSpacing: -0.5}}>{BRANDS[0].name}</div>
                  <div style={{fontSize: 17, color: TEXT, fontWeight: 500, whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis'}}>{BRANDS[0].offer}</div>
                </div>
                <span style={{fontSize: 28, color: ACCENT, fontWeight: 700, margin: '0 18px 0 0'}}>&#8594;</span>
              </div>
            )}

            {/* Step 3: Offer Details */}
            {step === 3 && showDetails && (
              <div style={{marginTop: 32, background: 'linear-gradient(90deg, #B4B8F8 0%, #FFD6A5 100%)', borderRadius: 24, boxShadow: '0 2px 12px 0 rgba(123,110,246,0.08)', border: `1.5px solid ${BORDER}`, overflow: 'hidden'}}>
                <div style={{padding: 26}}>
                  <div style={{display: 'flex', alignItems: 'center', gap: 14, marginBottom: 10}}>
                    <BrandIcon />
                    <div style={{fontWeight: 800, fontSize: 19}}>{BRANDS[0].name}</div>
                  </div>
                  <div style={{fontWeight: 800, fontSize: 20, marginBottom: 8}}>{BRANDS[0].offer}</div>
                  <div style={{fontSize: 17, color: SUBTLE, marginBottom: 12}}>{BRANDS[0].description}</div>
                  <div style={{fontSize: 15, color: SUBTLE, marginBottom: 10}}>Expires: {BRANDS[0].expires}</div>
                  <ul style={{fontSize: 15, color: SUBTLE, marginBottom: 16, paddingLeft: 20}}>
                    {BRANDS[0].conditions.map((c, i) => <li key={i}>{c}</li>)}
                  </ul>
                  <button
                    ref={copyCodeBtnRef}
                    style={{
                      width: '100%',
                      padding: '18px',
                      background: copied ? '#1EE0B7' : ACCENT,
                      color: '#fff',
                      border: 'none',
                      borderRadius: 14,
                      fontWeight: 700,
                      fontSize: 19,
                      marginTop: 8,
                      cursor: 'pointer',
                      transition: 'background 0.2s',
                    }}
                    onClick={() => {
                      navigator.clipboard.writeText(BRANDS[0].code);
                      setCopied(true);
                      setTimeout(() => setCopied(false), 1200);
                      setStep(4);
                    }}
                  >
                    {copied ? 'Copied!' : 'Copy Code'}
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
        {/* Floating Bottom Bar (with icons) */}
        <div
          style={{
            position: 'absolute',
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: 100,
            display: 'flex',
            justifyContent: 'center',
            background: 'rgba(255,255,255,0.97)',
            boxShadow: '0 -2px 16px 0 rgba(123,110,246,0.04)',
            padding: '0 0 0 0',
            borderBottomLeftRadius: RADIUS,
            borderBottomRightRadius: RADIUS,
            height: 72,
            alignItems: 'center',
          }}
        >
          <div style={{width: '100%', maxWidth: 420, display: 'flex', justifyContent: 'space-around', alignItems: 'center'}}>
            <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', flex: 1}}>
              <HomeIcon active={step === 0} />
              <span style={{fontSize: 11, color: step === 0 ? ACCENT : SUBTLE, fontWeight: 700, marginTop: 2}}>Home</span>
            </div>
            <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', flex: 1}}>
              <GiftIcon />
              <span style={{fontSize: 11, color: step === 2 ? ACCENT : SUBTLE, fontWeight: 700, marginTop: 2}}>Offers</span>
            </div>
            <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', flex: 1}}>
              <StarIcon />
              <span style={{fontSize: 11, color: step === 3 ? ACCENT : SUBTLE, fontWeight: 700, marginTop: 2}}>Code</span>
            </div>
            <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', flex: 1}}>
              <UserIcon />
              <span style={{fontSize: 11, color: SUBTLE, fontWeight: 700, marginTop: 2}}>Profile</span>
            </div>
          </div>
          {/* Floating CTA */}
          <div style={{position: 'absolute', left: 0, right: 0, bottom: 18, display: 'flex', justifyContent: 'center', pointerEvents: 'none'}}>
            {(step === 0 || step === 4) && (
              <button
                style={{
                  minWidth: 180,
                  padding: '18px 32px',
                  background: ACCENT,
                  color: '#fff',
                  border: 'none',
                  borderRadius: 22,
                  fontWeight: 800,
                  fontSize: 19,
                  boxShadow: '0 2px 8px rgba(123,110,246,0.10)',
                  cursor: 'pointer',
                  transition: 'background 0.2s',
                  pointerEvents: 'auto',
                  letterSpacing: 0.2,
                }}
                onClick={() => step === 0 ? setStep(1) : restart()}
              >
                {step === 0 ? 'Get Started' : 'Restart Demo'}
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
