import React, { useState, useEffect } from 'react';

const DommioDemo = () => {
  // State variables
  const [currentStep, setCurrentStep] = useState(0);
  const [showTooltip, setShowTooltip] = useState(true);
  const [favorites, setFavorites] = useState([2, 4]);
  const [searchQuery, setSearchQuery] = useState('');
  const [activeCategory, setActiveCategory] = useState('all');
  const [verified, setVerified] = useState(false);
  const [showVerificationModal, setShowVerificationModal] = useState(false);
  const [currentView, setCurrentView] = useState('home'); // 'home', 'product'
  const [currentProduct, setCurrentProduct] = useState(null);
  const [showSidebar, setShowSidebar] = useState(false);

  // Colors based on Dommio's CSS variables
  const colors = {
    primaryBlue: '#2D72D2',
    accentCoral: '#FF6B6B',
    accentTeal: '#1EE0B7',
    textColor: '#1A202C',
    textSecondary: '#4A5568',
    bgColor: '#F7FAFC', // Main app background
    bgLight: '#FFFFFF', // Card backgrounds, modals
    borderColor: '#E2E8F0',
  };

  // Categories with icons
  const categories = [
    { id: 'belleza', name: 'Belleza', icon: '‚úÇÔ∏è' },
    { id: 'comida', name: 'Comida', icon: 'üçï' },
    { id: 'educaci√≥n', name: 'Educaci√≥n', icon: 'üéí' },
    { id: 'entretenimiento', name: 'Entretenimiento', icon: 'üì∫' },
    { id: 'moda', name: 'Fashion', icon: 'üëï' },
    { id: 'lifestyle', name: 'Lifestyle', icon: 'üè†' },
    { id: 'tecnolog√≠a', name: 'Tecnolog√≠a', icon: 'üíª' },
    { id: 'viajes', name: 'Experiencias', icon: '‚úàÔ∏è' },
  ];

  // Demo offers - UPDATED PLACEHOLDER URLS
  const offers = [
    {
      id: 1,
      company: 'Spotify',
      logo: 'https://via.placeholder.com/60?text=S',
      title: '3 meses de Spotify Premium gratis',
      category: 'entretenimiento',
      discount: '100%',
      image: 'https://via.placeholder.com/300x180?text=Spotify+Premium',
      description: 'M√∫sica, podcasts y playlists sin anuncios, solo para estudiantes verificados.',
      details: 'Disfruta de Spotify Premium sin costo durante 3 meses. Sin publicidad, con descarga de canciones y reproducci√≥n sin conexi√≥n. Solo para nuevos usuarios de Premium que sean estudiantes verificados.',
      code: 'SPOTIFYEDU',
      expires: '31/12/2024',
      conditions: [
        'Solo para estudiantes verificados',
        'No acumulable con otras promociones',
        'Despu√©s de 3 meses, se aplicar√° el precio regular con descuento para estudiantes'
      ]
    },
    {
      id: 2,
      company: 'Dell',
      logo: 'https://via.placeholder.com/60?text=Dell',
      title: 'Hasta 20% de descuento en laptops',
      category: 'tecnolog√≠a',
      discount: '20%',
      image: 'https://via.placeholder.com/300x180?text=Dell+Laptops',
      description: 'Descuentos exclusivos en toda la l√≠nea de laptops para universitarios.',
      details: 'Obt√©n hasta 20% de descuento en laptops Dell selectas, perfectas para tus estudios. Incluye modelos Inspiron, XPS y G Series. Env√≠o gratis a toda la Rep√∫blica.',
      code: 'DELLEDU20',
      expires: '15/08/2024',
      conditions: [
        'V√°lido solo para modelos seleccionados',
        'Requiere verificaci√≥n estudiantil',
        'No acumulable con otras promociones',
        'M√°ximo 2 unidades por estudiante'
      ]
    },
    {
      id: 3,
      company: 'Cin√©polis',
      logo: 'https://via.placeholder.com/60?text=Cine',
      title: '2x1 en entradas de cine',
      category: 'entretenimiento',
      discount: '50%',
      image: 'https://via.placeholder.com/300x180?text=Cin√©polis',
      description: 'Entrada gratis para un acompa√±ante. V√°lido de lunes a jueves.',
      details: 'Disfruta del 2x1 en todas las salas tradicionales y salas 3D de Cin√©polis. V√°lido de lunes a jueves para estudiantes verificados.',
      code: 'CINE2X1EDU',
      expires: '31/12/2024',
      conditions: [
        'No v√°lido en VIP, IMAX, 4DX o Cin√©polis Pluus',
        'Sujeto a disponibilidad',
        'No acumulable con otras promociones',
        'No v√°lido en estrenos (primeras 2 semanas)'
      ]
    },
    {
      id: 4,
      company: 'Nike',
      logo: 'https://via.placeholder.com/60?text=Nike',
      title: '30% de descuento en calzado deportivo',
      category: 'moda',
      discount: '30%',
      image: 'https://via.placeholder.com/300x180?text=Nike+Air+Max',
      description: 'Descuento exclusivo para estudiantes en toda la l√≠nea de calzado.',
      details: 'Obt√©n 30% de descuento en calzado Nike, incluyendo las l√≠neas Air Max, React, y m√°s. V√°lido en tiendas f√≠sicas y en l√≠nea con tu c√≥digo de estudiante verificado.',
      code: 'NIKEEDU30',
      expires: '30/09/2024',
      conditions: [
        'Excluye productos de edici√≥n limitada',
        'V√°lido en tiendas f√≠sicas y nike.com.mx',
        'Requiere verificaci√≥n estudiantil',
        'No acumulable con otras promociones'
      ]
    },
    {
      id: 5,
      company: 'Uber Eats',
      logo: 'https://via.placeholder.com/60?text=Uber',
      title: 'Env√≠o gratis en tu primer pedido',
      category: 'comida',
      discount: 'Env√≠o Gratis',
      image: 'https://via.placeholder.com/300x180?text=Uber+Eats',
      description: 'Env√≠o gratis en pedidos mayores a $100 MXN para nuevos usuarios.',
      details: 'Recibe env√≠o gratis en tu primer pedido con Uber Eats. Pedido m√≠nimo de $100 MXN. Solo v√°lido para estudiantes que se registren por primera vez.',
      code: 'UBEREDU',
      expires: '31/12/2024',
      conditions: [
        'Solo para nuevos usuarios',
        'Pedido m√≠nimo de $100 MXN',
        'V√°lido una sola vez por cuenta',
        'Requiere verificaci√≥n estudiantil'
      ]
    },
    {
      id: 6,
      company: 'Amazon',
      logo: 'https://via.placeholder.com/60?text=Amazon',
      title: 'Prime Student a mitad de precio',
      category: 'tecnolog√≠a',
      discount: '50%',
      image: 'https://via.placeholder.com/300x180?text=Amazon+Prime',
      description: 'Membres√≠a Prime con 50% de descuento para estudiantes universitarios.',
      details: 'Obt√©n Amazon Prime a mitad de precio con tu verificaci√≥n estudiantil. Incluye env√≠os gratis, Prime Video, Prime Music y m√°s beneficios exclusivos.',
      code: 'PRIMEEDU',
      expires: '31/12/2024',
      conditions: [
        'Solo para estudiantes verificados',
        'V√°lido por 4 a√±os o hasta graduaci√≥n',
        'Renovaci√≥n autom√°tica al precio regular despu√©s del per√≠odo promocional',
        'Se requiere verificaci√≥n anual'
      ]
    }
  ];

  // Filter offers by search and category
  const filteredOffers = offers.filter(offer => {
    const matchesSearch = searchQuery === '' ||
      offer.company.toLowerCase().includes(searchQuery.toLowerCase()) ||
      offer.title.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesCategory = activeCategory === 'all' || offer.category === activeCategory;

    return matchesSearch && matchesCategory;
  });

  // Demo steps
  const steps = [
    {
      title: '¬°Bienvenido a Dommio!',
      description: 'La plataforma definitiva para descuentos exclusivos para estudiantes en M√©xico. Haz clic en "Siguiente" para explorar c√≥mo funciona.',
      target: null,
      position: 'center',
    },
    {
      title: 'Verificaci√≥n Estudiantil',
      description: 'El primer paso es verificar tu estatus como estudiante para acceder a todos los descuentos exclusivos.',
      target: '.verification-btn', // This class will be on the main "Verificarme" button
      position: 'top', // Adjusted for a button at the bottom right
    },
    {
      title: 'Explora Categor√≠as',
      description: 'Filtra descuentos por categor√≠as como Tecnolog√≠a, Comida, Entretenimiento y m√°s.',
      target: '.categories',
      position: 'bottom',
    },
    {
      title: 'Busca Descuentos',
      description: 'Encuentra r√°pidamente ofertas por nombre, marca o palabra clave.',
      target: '.search-bar',
      position: 'bottom',
    },
    {
      title: 'Guarda tus Favoritos',
      description: 'Marca las ofertas que te interesen para acceder a ellas f√°cilmente despu√©s. (El tooltip apuntar√° al primer bot√≥n de favorito visible).',
      target: '.favorite-btn',
      position: 'right',
    },
    {
      title: 'Utiliza los Descuentos',
      description: 'Accede a detalles completos de la oferta y obt√©n el c√≥digo o enlace para usarla. (El tooltip apuntar√° al primer bot√≥n "OBTENER" visible).',
      target: '.use-offer-btn',
      position: 'bottom',
    },
    {
      title: '¬°Listo para Ahorrar!',
      description: 'Ya conoces lo b√°sico de Dommio. Explora la demo libremente o regresa al tutorial en cualquier momento.',
      target: null,
      position: 'center',
    }
  ];

  // Toggle favorite status
  const toggleFavorite = (id) => {
    setFavorites(prevFavorites =>
      prevFavorites.includes(id)
        ? prevFavorites.filter(favId => favId !== id)
        : [...prevFavorites, id]
    );
  };

  // Open product page
  const openProductPage = (id) => {
    setCurrentProduct(id);
    setCurrentView('product');
  };

  // Go back to home
  const goBackToHome = () => {
    setCurrentView('home');
    setCurrentProduct(null);
  };

  // Toggle sidebar menu
  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  // Handle end of tutorial
  useEffect(() => {
    if (currentStep >= steps.length) {
      setShowTooltip(false);
    }
  }, [currentStep, steps.length]);

  // Verification modal
  const VerificationModal = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg p-6 max-w-md w-full" style={{ backgroundColor: colors.bgLight }}>
        <h3 className="text-xl font-bold mb-4" style={{ color: colors.textColor }}>Verificaci√≥n Estudiantil</h3>
        <p className="mb-4" style={{ color: colors.textSecondary }}>Para acceder a todos los descuentos, necesitamos verificar que eres estudiante.</p>
        <div className="mb-4">
          <label className="block text-sm font-medium mb-1" style={{ color: colors.textSecondary }}>Universidad</label>
          <select className="w-full p-2 border rounded" style={{ borderColor: colors.borderColor, backgroundColor: 'white' }}>
            <option>UNAM</option>
            <option>Tec de Monterrey</option>
            <option>UAM</option>
            <option>IPN</option>
            <option>IBERO</option>
            <option>Otra...</option>
          </select>
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium mb-1" style={{ color: colors.textSecondary }}>Correo Institucional</label>
          <input type="email" placeholder="tucorreo@universidad.edu.mx" className="w-full p-2 border rounded" style={{ borderColor: colors.borderColor, backgroundColor: 'white' }} />
        </div>
        <div className="flex justify-end space-x-2">
          <button
            onClick={() => setShowVerificationModal(false)}
            className="px-4 py-2 border rounded"
            style={{ borderColor: colors.borderColor, color: colors.textSecondary }}
          >
            Cancelar
          </button>
          <button
            onClick={() => {
              setVerified(true);
              setShowVerificationModal(false);
              if (currentStep === 1) setCurrentStep(2); // Step 1 is verification button
            }}
            className="px-4 py-2 rounded text-white"
            style={{ backgroundColor: colors.accentCoral }}
          >
            Verificar
          </button>
        </div>
      </div>
    </div>
  );

  // Tooltip component
  const Tooltip = () => {
    if (!showTooltip || currentStep >= steps.length) return null;

    const step = steps[currentStep];
    let tooltipStyle = {
        position: 'absolute',
        pointerEvents: 'auto',
        backgroundColor: colors.bgLight,
        borderColor: colors.borderColor,
        boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
        zIndex: 1000, // Ensure tooltip is above other elements
    };
    let baseTooltipClass = "p-4 rounded-lg shadow-lg w-64";

    if (step.target && document.querySelector(step.target)) {
        const targetEl = document.querySelector(step.target);
        if (!targetEl) return null; // Target not found
        const rect = targetEl.getBoundingClientRect();
        const tooltipWidth = 256; // w-64
        const tooltipHeightEstimate = 150; // Estimate, or calculate if needed
        const margin = 10;

        switch(step.position) {
            case 'top':
                tooltipStyle.bottom = window.innerHeight - rect.top + margin;
                tooltipStyle.left = rect.left + rect.width / 2 - tooltipWidth / 2;
                break;
            case 'bottom':
                tooltipStyle.top = rect.bottom + margin;
                tooltipStyle.left = rect.left + rect.width / 2 - tooltipWidth / 2;
                break;
            case 'left':
                tooltipStyle.right = window.innerWidth - rect.left + margin;
                tooltipStyle.top = rect.top + rect.height / 2 - tooltipHeightEstimate / 2;
                break;
            case 'right':
                tooltipStyle.left = rect.right + margin;
                tooltipStyle.top = rect.top + rect.height / 2 - tooltipHeightEstimate / 2;
                break;
            default: // center, or fallback
                return (
                    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[1000] p-4">
                        <div className="p-6 rounded-lg max-w-md" style={{backgroundColor: colors.bgLight}}>
                            <h3 className="text-2xl font-bold mb-2" style={{ color: colors.primaryBlue }}>{step.title}</h3>
                            <p className="mb-6" style={{ color: colors.textSecondary }}>{step.description}</p>
                            <div className="flex justify-between items-center">
                                {currentStep > 0 && (
                                <button
                                    onClick={() => setCurrentStep(currentStep - 1)}
                                    className="px-4 py-2 rounded border"
                                    style={{ borderColor: colors.borderColor, color: colors.textSecondary }}
                                >
                                    Anterior
                                </button>
                                )}
                                <div className="flex-grow"></div>
                                <button
                                onClick={() => setCurrentStep(currentStep + 1)}
                                className="px-4 py-2 rounded text-white"
                                style={{ backgroundColor: colors.accentCoral }}
                                >
                                {currentStep < steps.length - 1 ? 'Siguiente' : 'Finalizar'}
                                </button>
                            </div>
                        </div>
                    </div>
                );
        }
        // Boundary checks (simplified)
        if (tooltipStyle.left < margin) tooltipStyle.left = margin;
        if (tooltipStyle.left + tooltipWidth > window.innerWidth - margin) {
            tooltipStyle.left = window.innerWidth - tooltipWidth - margin;
        }

        return (
            <div className="fixed inset-0 z-[999] pointer-events-none"> {/* z-index slightly lower than modal */}
                <div
                    className={baseTooltipClass}
                    style={tooltipStyle}
                >
                    <h4 className="font-bold text-lg mb-2" style={{ color: colors.primaryBlue }}>{step.title}</h4>
                    <p className="text-sm mb-4" style={{ color: colors.textSecondary }}>{step.description}</p>
                    <div className="flex justify-between items-center">
                        {currentStep > 0 && (
                        <button
                            onClick={() => setCurrentStep(currentStep - 1)}
                            className="px-3 py-1 text-sm rounded border"
                            style={{ borderColor: colors.borderColor, color: colors.textSecondary }}
                        >
                            Anterior
                        </button>
                        )}
                        <div className="flex-grow"></div>
                        <button
                        onClick={() => {
                            if (currentStep === 1 && !verified) { // Step 1 is verification
                            setShowVerificationModal(true);
                            } else {
                            setCurrentStep(currentStep + 1);
                            }
                        }}
                        className="px-3 py-1 text-sm rounded text-white"
                        style={{ backgroundColor: colors.accentCoral }}
                        >
                        {currentStep < steps.length - 1 ? 'Siguiente' : 'Finalizar'}
                        </button>
                    </div>
                </div>
            </div>
        );

    }

    // Default to center tooltip if target is null or not found
    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[1000] p-4">
            <div className="p-6 rounded-lg max-w-md" style={{backgroundColor: colors.bgLight}}>
                <h3 className="text-2xl font-bold mb-2" style={{ color: colors.primaryBlue }}>{step.title}</h3>
                <p className="mb-6" style={{ color: colors.textSecondary }}>{step.description}</p>
                <div className="flex justify-between items-center">
                    {currentStep > 0 && (
                    <button
                        onClick={() => setCurrentStep(currentStep - 1)}
                        className="px-4 py-2 rounded border"
                        style={{ borderColor: colors.borderColor, color: colors.textSecondary }}
                    >
                        Anterior
                    </button>
                    )}
                    <div className="flex-grow"></div>
                    <button
                    onClick={() => setCurrentStep(currentStep + 1)}
                    className="px-4 py-2 rounded text-white"
                    style={{ backgroundColor: colors.accentCoral }}
                    >
                    {currentStep < steps.length - 1 ? 'Siguiente' : 'Finalizar'}
                    </button>
                </div>
            </div>
        </div>
    );
  };

  // Home View
  const HomeView = () => (
    <div>
      {/* Search and Filter */}
      <div className="mb-4">
        <div className="relative search-bar">
          <input
            type="text"
            placeholder="Buscar tiendas, productos..."
            className="w-full px-4 py-3 rounded-full border"
            style={{ borderColor: colors.borderColor, backgroundColor: '#f0f2f5', color: colors.textColor }}
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button
            className="absolute right-4 top-0 h-full flex items-center justify-center"
            style={{ color: colors.textSecondary }}
            aria-label="Buscar"
          >
            üîç
          </button>
        </div>
      </div>

      {/* Categories */}
      <div className="mb-6 categories">
        <div className="grid grid-cols-4 gap-2 sm:grid-cols-4">
          {categories.map((category) => ( // Show all categories
            <div
              key={category.id}
              className="flex flex-col items-center p-1 cursor-pointer transition-transform hover:scale-105"
              onClick={() => setActiveCategory(category.id === activeCategory ? 'all' : category.id)}
            >
              <div
                className="w-12 h-12 rounded-lg flex items-center justify-center mb-1 transition-colors"
                style={{
                  backgroundColor: category.id === activeCategory ? colors.primaryBlue : '#f0f2f5',
                  color: category.id === activeCategory ? 'white' : colors.textColor
                }}
              >
                <span className="text-2xl">{category.icon}</span>
              </div>
              <span className="text-xs font-medium text-center" style={{ color: colors.textSecondary }}>{category.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Offers List (Filtered) */}
      <div>
        <div className="flex justify-between items-center mb-2">
            <h2 className="text-xl font-bold" style={{ color: colors.textColor }}>
                {activeCategory === 'all' ? 'Todas las Ofertas' : categories.find(c => c.id === activeCategory)?.name || 'Ofertas'}
            </h2>
            <span className="text-sm font-medium" style={{ color: colors.textSecondary }}>{filteredOffers.length} ofertas</span>
        </div>

        {filteredOffers.length > 0 ? (
            <div className="space-y-4">
            {filteredOffers.map((offer) => (
                <div
                key={offer.id}
                className="bg-white rounded-lg overflow-hidden shadow-md flex cursor-pointer hover:shadow-lg transition-shadow"
                style={{ backgroundColor: colors.bgLight }}
                onClick={() => openProductPage(offer.id)}
                >
