                    onClick={(e) => {
                        e.stopPropagation();
                        if (verified) {
                        openProductPage(offer.id);
                        } else {
                        setShowVerificationModal(true);
                        if(currentStep === 1) setCurrentStep(2); // Advance tutorial if stuck on verification
                        }
                    }}
                    disabled={!verified && currentStep < 2} // Disable if not verified and tutorial hasn't passed verification step
                    >
                    {verified ? 'VER OFERTA' : 'VERIFICAR PARA USAR'}
                    </button>
                </div>
                </div>
            ))}
            </div>
        ) : (
            <p className="text-center py-8" style={{ color: colors.textSecondary }}>No hay ofertas que coincidan con tu b√∫squeda.</p>
        )}
        </div>
    </div>
  );

  // Product Detail View
  const ProductView = () => {
    if (!currentProduct) return null;

    const offer = offers.find(o => o.id === currentProduct);
    if (!offer) return null;

    return (
      <div style={{ backgroundColor: colors.bgLight }} className="rounded-lg shadow-lg">
        <div className="relative">
          <img
            src={offer.image}
            alt={offer.title}
