          />
          <button
            className="absolute top-3 left-3 text-gray-700 bg-white/80 hover:bg-white px-3 py-1 rounded-md shadow text-sm"
            onClick={goBackToHome}
            aria-label="Regresar"
          >
            ‚Üê Regresar
          </button>
          <button
            className="absolute top-3 right-3 text-3xl"
            style={{
              color: favorites.includes(offer.id) ? colors.accentCoral : 'rgba(0,0,0,0.3)',
              textShadow: '0 0 8px white'
            }}
            onClick={() => toggleFavorite(offer.id)}
            aria-label={favorites.includes(offer.id) ? "Quitar de favoritos" : "Agregar a favoritos"}
          >
