          </div>
          <h3 className="text-xl font-semibold mb-2" style={{ color: colors.primaryBlue }}>{offer.title}</h3>
          <p className="text-sm mb-4 leading-relaxed" style={{ color: colors.textSecondary }}>{offer.details}</p>

          {verified ? (
            <div className="mb-6">
              <h4 className="text-md font-semibold mb-2" style={{ color: colors.textColor }}>C√≥digo de Descuento:</h4>
              <div className="bg-gray-100 p-3 rounded-md mb-2 flex items-center justify-between border" style={{ borderColor: colors.borderColor }}>
                <span className="font-mono font-bold text-lg" style={{ color: colors.primaryBlue }}>{offer.code}</span>
                <button
                  className="text-xs font-bold px-3 py-1.5 rounded text-white"
                  style={{ backgroundColor: colors.accentTeal }}
                  onClick={() => navigator.clipboard.writeText(offer.code).then(() => alert('C√≥digo copiado!'))}
                >
                  COPIAR
                </button>
              </div>
              <p className="text-xs" style={{ color: colors.textSecondary }}>V√°lido hasta: {offer.expires}</p>
            </div>
          ) : (
            <div className="mb-6 bg-yellow-50 border border-yellow-200 p-4 rounded-md text-center">
              <p className="text-sm font-semibold mb-1 text-yellow-700">¬°Verif√≠cate para obtener el c√≥digo!</p>
              <p className="text-xs mb-3 text-yellow-600">Verifica tu estatus de estudiante para ver y usar este c√≥digo de descuento.</p>
              <button
                className="px-4 py-2 rounded text-white text-sm font-bold"
                style={{ backgroundColor: colors.accentCoral }}
                onClick={() => {
                    setShowVerificationModal(true);
                    if(currentStep === 1) setCurrentStep(2); // Advance tutorial
                }}
              >
                Verificar Ahora
              </button>
            </div>
          )}

          <div className="mb-4">
            <h4 className="text-md font-semibold mb-2" style={{ color: colors.textColor }}>Condiciones:</h4>
            <ul className="text-sm space-y-1.5 list-disc pl-5" style={{ color: colors.textSecondary }}>
              {offer.conditions.map((condition, index) => (
                <li key={index}>{condition}</li>
              ))}
            </ul>
          </div>

          <button
            className="w-full py-3 mt-4 rounded text-center text-white text-lg font-bold"
            style={{ backgroundColor: verified ? colors.primaryBlue : '#CBD5E0' }}
            onClick={() => {
              if (verified) {
                alert(`Usando oferta: ${offer.title} con c√≥digo ${offer.code}`);
                // Here you would typically redirect or show more info
              } else {
                setShowVerificationModal(true);
                if(currentStep === 1) setCurrentStep(2);
              }
            }}
            disabled={!verified}
          >
            {verified ? 'USAR DESCUENTO' : 'VERIFICA PARA USAR'}
          </button>
        </div>
      </div>
    );
  };


  // MAIN RENDER FOR DommioDemo
  return (
    <div className="font-sans antialiased" style={{ backgroundColor: colors.bgColor, color: colors.textColor, minHeight: '100vh' }}>
      <div className="container mx-auto p-4 max-w-2xl"> {/* Simulating a mobile/tablet view width */}
        {/* Header */}
        <header className="flex justify-between items-center py-4 mb-4 sticky top-0 z-20" style={{ backgroundColor: colors.bgColor }}>
          <button onClick={toggleSidebar} className="text-2xl p-2 -ml-2" aria-label="Abrir men√∫">
            <span style={{color: colors.primaryBlue}}>‚ò∞</span>
          </button>
          <h1 className="text-3xl font-bold tracking-tight" style={{ color: colors.primaryBlue }}>
            Dommio
          </h1>
          <div className="relative">
            <button className="text-2xl p-2 -mr-2" aria-label="Notificaciones">
                <span style={{color: colors.primaryBlue}}>üîî</span>
            </button>
            {/* Placeholder for notification count */}
            <span className="absolute top-1 right-1 bg-red-500 text-white text-xs font-semibold rounded-full w-4 h-4 flex items-center justify-center pointer-events-none">3</span>
          </div>
        </header>

        {/* Sidebar (Overlay) */}
        {showSidebar && (
          <>
            <div className="fixed inset-0 bg-black bg-opacity-50 z-30" onClick={toggleSidebar}></div>
            <div
