            >
              <button onClick={toggleSidebar} className="absolute top-4 right-4 text-2xl" style={{color: colors.textSecondary}} aria-label="Cerrar men√∫">√ó</button>
              <h2 className="text-2xl font-bold mb-6" style={{color: colors.primaryBlue}}>Men√∫</h2>
              <ul className="space-y-3">
                {[
                    {label: "Inicio", action: () => {goBackToHome(); setShowSidebar(false);}},
                    {label: "Mi Perfil", action: () => setShowSidebar(false)},
                    {label: "Mis Favoritos", action: () => setShowSidebar(false)},
                    {label: "Ajustes", action: () => setShowSidebar(false)},
                    {label: "Reiniciar Tutorial", action: () => {setCurrentStep(0); setShowTooltip(true); setShowSidebar(false); goBackToHome();}},
                    {label: "Cerrar Sesi√≥n", action: () => setShowSidebar(false)},
                ].map(item => (
                    <li key={item.label}>
                        <a href="#" onClick={(e) => {e.preventDefault(); item.action();}} className="block py-2 px-1 rounded hover:bg-gray-100 transition-colors" style={{color: colors.textSecondary}}>
                            {item.label}
                        </a>
                    </li>
                ))}
              </ul>
            </div>
          </>
        )}

        {/* Main Content Area */}
        <main className="pb-16"> {/* Padding bottom for floating button */}
          {currentView === 'home' && <HomeView />}
          {currentView === 'product' && <ProductView />}
        </main>

        {/* Floating Verification Button */}
        {currentView === 'home' && !verified && currentStep > 1 && ( // Show if not verified and past initial tutorial steps
