        )}


        {/* Modals & Tooltips - Render these last to ensure they are on top */}
        {showVerificationModal && <VerificationModal />}
        <Tooltip /> {/* Tooltip manages its own visibility based on showTooltip and currentStep */}

      </div>
    </div>
  );
};

export default DommioDemo;
